import React, { Component } from 'react'
import DropdownButton from 'react-bootstrap/DropdownButton'
import Dropdown from 'react-bootstrap/Dropdown'

export class AddFolder extends Component {
  state = {
    title: '',
    path: './root',
    type: '',
    children: []
  }

  onSubmitFile = (e1) => {
      e1.preventDefault();
      this.props.addFolder(this.state.title, this.state.path, 'file', []);
      this.setState({ title: '' });
  }

  onSubmitFolder = (e2) => {
    e2.preventDefault();
    this.props.addFolder(this.state.title, this.state.path, 'folder', []);
    this.setState({ title: '' });
}

  onChangeFile = (e1) => this.setState({ [e1.target.name]: e1.target.value });
  onChangeFolder = (e2) => this.setState({ [e2.target.name]: e2.target.value });

  render() {
    return (
      <DropdownButton id="dropdown-basic-button" title="New" style={{float: 'right'}}>
        <Dropdown.Item onSubmitFile={this.onSubmitFile} style={{display: 'flex'}}>
          <input
            type="text"
            name="title"
            style={{ flex: '10', padding: '5px' }} 
            placeholder="Add File ..."
            value = {this.state.title}
            onChangeFile={this.onChangeFile}
          />
          <input 
            type="submit" 
            value="Submit"
            className="btn"
            style={{flex: '1'}} 
          />
        </Dropdown.Item>
        <Dropdown.Item onSubmitFolder={this.onSubmitFolder} style={{display: 'flex'}}>
          <input
            type="text"
            name="title"
            style={{ flex: '10', padding: '5px' }} 
            placeholder="Add Folder ..."
            value = {this.state.title}
            onChangeFolder={this.onChangeFolder}
          />
          <input 
            type="submit" 
            value="Submit"
            className="btn"
            style={{flex: '1'}} 
          />
        </Dropdown.Item>
      </DropdownButton>

      // <form onSubmit={this.onSubmit} style={{display: 'flex' }}>
      //     <input 
      //       type="text" 
      //       name="title"
      //       style={{ flex: '10', padding: '5px' }} 
      //       placeholder="Add Folder ..."
      //       value = {this.state.title}
      //       onChange={this.onChange}
      //     />
      //     <input 
      //       type="submit" 
      //       value="Submit"
      //       className="btn"
      //       style={{flex: '1'}} 
      //     />
      // </form>
    )
  }
}

export default AddFolder
