import React, { Component } from 'react';
import Folders from './Components/Folders';

import './App.css';

class App extends Component {
  state = {
    folders: [
      {
        id: 1,
        title: 'Hello1'
      },
      {
        id: 2,
        title: 'Hello2'
      },
      {
        id: 3,
        title: 'Hello3'
      }
    ]
  }

  // Delete Folder
  delFolder = (id) => {
    this.setState({ folders: [...this.state.todos.filter(todo => todo.id
      !== id)] });
  }

  render() {
    return (
      <div className="App">
      {/* Prop */}
      <Folders folders={this.state.folders} 
      delFolder={this.delFolder}/>

      </div>
    );
  }
}

export default App;
