import React, { Component } from 'react'
import DropdownButton from 'react-bootstrap/DropdownButton'
import Dropdown from 'react-bootstrap/Dropdown'

export class AddFolder extends Component {
  state = {
    title: ''
  }

  onSubmit = (e) => {
      e.preventDefault();
      this.props.addFolder(this.state.title);
      this.setState({ title: ''});
  }

  onChange = (e) => this.setState({ [e.target.name]: e.target.value });

  render() {
    return (
      <DropdownButton id="dropdown-basic-button" title="New">
        <Dropdown.Item onSubmitFile={this.onSubmitFile} style={{display: 'flex'}}>
          <input
            type="text"
            style={{ flex: '10', padding: '5px' }} 
            placeholder="Add File ..."
            value = {this.state.title}
            onChange={this.onChange}
          />
          <input 
            type="submit" 
            value="Submit"
            className="btn"
            style={{flex: '1'}} 
          />
        </Dropdown.Item>
        <Dropdown.Item onSubmitFolder={this.onSubmitFolder} style={{display: 'flex'}}>
          <input
            type="text"
            style={{ flex: '10', padding: '5px' }} 
            placeholder="Add Folder ..."
            value = {this.state.title}
            onChange={this.onChange}
          />
          <input 
            type="submit" 
            value="Submit"
            className="btn"
            style={{flex: '1'}} 
          />
        </Dropdown.Item>
      </DropdownButton>

      // <form onSubmit={this.onSubmit} style={{display: 'flex' }}>
      //     <input 
      //       type="text" 
      //       name="title"
      //       style={{ flex: '10', padding: '5px' }} 
      //       placeholder="Add Folder ..."
      //       value = {this.state.title}
      //       onChange={this.onChange}
      //     />
      //     <input 
      //       type="submit" 
      //       value="Submit"
      //       className="btn"
      //       style={{flex: '1'}} 
      //     />
      // </form>
    )
  }
}

export default AddFolder
